mapper.update:
UPDATE linker.card

	title = #{title}
	content = #{content}

WHERE id = #{id}

UPDATE linker.card c
INNER JOIN linker.cardlist cl ON cl.id = c.cl_id

	c.ps_id = #{ps_id}

WHERE c.id = #{id}

mapper.delete:
DELETE FROM linker.user_has_card 
WHERE u_id = #{u_id} 
AND c_id = #{c_id}
