mapper.select:
 SELECT count(email)
FROM linker.user AS u
WHERE email= #{email};

        SELECT id as u_id, nickname, email
		FROM linker.user
		WHERE email= #{email}

mapper.delete:
DELETE FROM linker.team_has_user
WHERE u_id=#{u_id}
AND t_id=#{t_id}

mapper.update:
UPDATE linker.team_has_user
SET auth = #{auth}
WHERE u_id=#{u_id}
AND t_id=#{t_id}

UPDATE linker.team_has_user
SET status = 1
WHERE u_id=#{u_id}
AND t_id=#{t_id}
