mapper.update:
UPDATE linker.user

	nickname=#{nickname},
	
	profile=#{profile} 

WHERE email = #{email}

mapper.select:
SELECT count(email)
FROM linker.user
WHERE email = #{email}

mapper.update:
UPDATE linker.user
SET password = #{password}
WHERE email=#{email}

mapper.select:
 select count(email)
 from linker.user
 where email=#{email}

 SELECT count(*)
 FROM linker.user
 WHERE email=#{email} AND password=#{password}

mapper.update:
  UPDATE linker.user
  SET password = #{password}
  WHERE email=#{email}

mapper.select:
SELECT password
FROM linker.user
WHERE email = #{email}

mapper.update:

 UPDATE linker.user
 SET status = 2 , ddate=now()
 WHERE email = #{email}
