mapper.update:
UPDATE user

	nickname=#{nickname},
	
	profile=#{profile} 

WHERE email = #{email}

mapper.select:
SELECT count(email)
FROM user
WHERE email = #{email}

mapper.update:
UPDATE user
SET password = #{password}
WHERE email=#{email}

mapper.select:
 select count(email)
 from user
 where email=#{email}

 SELECT count(*)
 FROM user
 WHERE email=#{email} AND password=#{password}

mapper.update:
  UPDATE user
  SET password = #{password}
  WHERE email=#{email}

mapper.select:
SELECT password
FROM user
WHERE email = #{email}

mapper.update:

 UPDATE user
 SET status = 2 , ddate=now()
 WHERE email = #{email}
